mapper.select:
select b.c_id as b_id, b.c_pn as b_pn, b.c_name as b_name, b.c_type as b_type, u.c_id as u_id, u.c_name as u_name, u.c_sub_name as u_sub_name, u.c_ratio as u_ratio, s.c_id as s_id, s.c_name as s_name from bom_tbl as b left join unit_tbl as u on b.c_unit_id=u.c_id left join bom_supplier_tbl as bs on b.c_id=bs.c_bom_id left join supplier_tbl as s on bs.c_supplier_id=s.c_id
and b.c_id = #{id}
and b.c_pn like '%${pn}%'
and b.c_pn = #{pn}
and b.c_type = #{type}
order by b.c_type

mapper.update:
    	update bom_tbl
		set	c_pn = #{pn},
			c_name = #{name},
			c_unit_id = #{unit.id}

			c_id = #{id}

if.foreach:
delete from bom_supplier_tbl

	c_bom_id=#{id} and
	c_supplier_id=#{supplier.id}

