mapper.select:
select m.c_id as m_id, m.c_pn as m_pn, m.c_name as m_name, m.c_maitenace_period as m_maitenace_period, u.c_id as u_id, u.c_name as u_name, s.c_id as s_id, s.c_name as s_name, mp_rel.c_maitenace_part_num as mp_rel_maitenace_part_num, mp.c_id as mp_id, mp.c_pn as mp_pn, mp.c_name as mp_name, mp_u.c_id as mp_u_id, mp_u.c_name as mp_u_name from machine_tbl m left join unit_tbl as u on m.c_unit_id = u.c_id left join supplier_tbl as s on m.c_supplier_id = s.c_id left join machine_part_relation_tbl as mp_rel on m.c_id = mp_rel.c_machine_id left join machine_part_tbl as mp on mp_rel.c_machine_part_id = mp.c_id left join unit_tbl as mp_u on mp.c_unit_id = mp_u.c_id
and m.c_id = #{id}
and m.c_pn = #{pn}
