mapper.select:
select order_pn.c_id as order_pn_id,
       order_pn.c_num as order_pn_num,
  	   order_pn.c_reject_ratio as order_pn_reject_ratio 
from order_pn_tbl order_pn 


		and order_pn.c_order_id = #{order.id} 


		and order_pn.c_pn_id = #{pn.id} 


		and order_pn.c_pn_cls_id = #{pn.pnCls.id}

select SUM(order_pn.c_num * pn_bom_relation.c_use_num) as bom_num, CONCAT(bom.c_pn," ",bom.c_name) as bom_pn_name, bom.c_type as bom_type, bom_ut.c_name as bom_ut_name, wh_bom.c_num as wh_bom_num, supplier.c_id as supplier_id, supplier.c_name as supplier_name from order_pn_tbl as order_pn left join pn_tbl as pn on order_pn.c_pn_id = pn.c_id left join pn_cls_tbl as pn_cls on order_pn.c_pn_cls_id = pn_cls.c_id left join pn_bom_relation_tbl as pn_bom_relation on pn_bom_relation.c_pn_id = pn.c_id left join bom_tbl as bom on pn_bom_relation.c_bom_id = bom.c_id left join unit_tbl as bom_ut on bom.c_unit_id = bom_ut.c_id left join warehouse_bom_tbl as wh_bom on wh_bom.c_bom_id = bom.c_id left join supplier_tbl as supplier on bom.c_supplier_id = supplier.c_id
and order_pn.c_order_id = #{id}
group by bom_pn_name, bom_type, bom_ut_name, wh_bom_num, supplier_id, supplier_name
