mapper.select:
select order_pn.c_id as order_pn_id, order_pn.c_num as order_pn_num, order_pn.c_delivered_num as order_pn_delivered_num, pn.c_id as pn_id, pn.c_pn as pn_pn, pn.c_name as pn_name, ut.c_id as ut_id, ut.c_name as ut_name, wh_pn.c_id as wh_pn_id, wh_pn.c_produced_num as wh_pn_produced_num, wh_pn.c_semi_produced_num as wh_pn_semi_produced_num from order_pn_tbl order_pn left join pn_tbl as pn on order_pn.c_pn_id = pn.c_id left join unit_tbl as ut on pn.c_unit_id = ut.c_id left join warehouse_pn_tbl as wh_pn on wh_pn.c_pn_id = pn.c_id
and order_pn.c_id=#{id}
and order_pn.c_order_id = #{order.id}
and order_pn.c_pn_id = #{pn.id}
