mapper.select:
select pn_cls.c_id as c_id, pn_cls.c_name as c_name, pn_cls_bom_rel.c_id as pn_cls_bom_rel_id, pn_cls_bom_rel.c_use_num as pn_cls_bom_rel_use_num, bom.c_id as bom_id, bom.c_pn as bom_pn, bom.c_name as bom_name, bom.c_type as bom_type, bom.c_price as bom_price, bom_unit.c_id as bom_ut_id, bom_unit.c_name as bom_ut_name, bom_unit.c_sub_name as bom_ut_sub_name, bom_unit.c_ratio as bom_ut_ratio from pn_cls_tbl as pn_cls left join pn_cls_bom_relation_tbl as pn_cls_bom_rel on pn_cls.c_id = pn_cls_bom_rel.c_cls_id left join bom_tbl as bom on pn_cls_bom_rel.c_bom_id = bom.c_id left join unit_tbl as bom_unit on bom.c_unit_id = bom_unit.c_id
and pn_cls.c_id = #{id}
and pn_cls.c_name = #{name}
order by pn_cls.c_name asc

mapper.insert:
		insert into pn_cls_tbl
		(
			c_name
		)
		values
		(
			#{name}
		)

      		select LAST_INSERT_ID() as id

mapper.delete:
delete from pn_cls_bom_relation_tbl

	c_cls_id=#{id} and c_bom_id in

		#{pnClsBOMRel.bom.id}
