mapper.select:
select pn.c_id as pn_id, pn.c_pn as pn_pn, pn.c_name as pn_name, ut.c_id as ut_id, ut.c_name as ut_name, ut.c_sub_name as ut_sub_name, ut.c_ratio as ut_ratio, pn_bom_rel.c_id as pn_bom_rel_id, pn_bom_rel.c_use_num as pn_bom_rel_use_num, pn_bom.c_id as pn_bom_id, pn_bom.c_pn as pn_bom_pn, pn_bom.c_name as pn_bom_name, pn_bom.c_type as pn_bom_type, pn_bom.c_price as pn_bom_price, pn_bom_unit.c_id as pn_bom_ut_id, pn_bom_unit.c_name as pn_bom_ut_name, pn_bom_unit.c_sub_name as pn_bom_ut_sub_name, pn_bom_unit.c_ratio as pn_bom_ut_ratio, pn_pn_cls.c_id as pn_pn_cls_id, pn_pn_cls.c_num as pn_pn_cls_num, pn_cls.c_id as pn_cls_id, pn_cls.c_name as pn_cls_name, pn_cls_bom_rel.c_id as pn_cls_bom_rel_id, pn_cls_bom_rel.c_use_num as pn_cls_bom_rel_use_num, bom.c_id as bom_id, bom.c_pn as bom_pn, bom.c_name as bom_name, bom.c_type as bom_type, bom.c_price as bom_price, bom_unit.c_id as bom_ut_id, bom_unit.c_name as bom_ut_name, bom_unit.c_sub_name as bom_ut_sub_name, bom_unit.c_ratio as bom_ut_ratio from pn_tbl as pn left join unit_tbl as ut on pn.c_unit_id = ut.c_id left join pn_bom_relation_tbl as pn_bom_rel on pn.c_id = pn_bom_rel.c_pn_id left join bom_tbl as pn_bom on pn_bom_rel.c_bom_id = pn_bom.c_id left join unit_tbl as pn_bom_unit on pn_bom.c_unit_id = pn_bom_unit.c_id left join pn_cls_relation_tbl as pn_pn_cls on pn.c_id = pn_pn_cls.c_pn_id left join pn_cls_tbl as pn_cls on pn_pn_cls.c_cls_id = pn_cls.c_id left join pn_cls_bom_relation_tbl as pn_cls_bom_rel on pn_cls.c_id = pn_cls_bom_rel.c_cls_id left join bom_tbl as bom on pn_cls_bom_rel.c_bom_id = bom.c_id left join unit_tbl as bom_unit on bom.c_unit_id = bom_unit.c_id
and pn.c_id = #{id}
and pn.c_pn like '%${pn}%'
and pn.c_pn = #{pn}
order by pn.c_pn asc, pn_cls.c_id asc, bom.c_type asc

delete.foreach:
delete from pn_cls_relation_tbl

	c_pn_id=#{id} and
	c_cls_id=#{pnClsRel.pnCls.id}

mapper.delete:
delete from pn_bom_relation_tbl

	c_pn_id=#{id} and c_bom_id in

		#{pnBOMRel.bom.id}

mapper.update:
    	update pn_tbl
		set	c_pn = #{pn},
			c_name = #{name},
			c_unit_id = #{unit.id}

			c_id = #{id}
