mapper.select:
select pn.c_id as pn_id, pn.c_pn as pn_pn, pn.c_name as pn_name, ut.c_id as ut_id, ut.c_name as ut_name, ut.c_sub_name as ut_sub_name, ut.c_ratio as ut_ratio, pn_cls.c_id as pn_cls_id, pn_cls.c_name as pn_cls_name, pn_bom_rel.c_id as pn_bom_rel_id, pn_bom_rel.c_use_num as pn_bom_rel_use_num, pn_bom_rel.c_price as pn_bom_rel_price, bom.c_id as bom_id, bom.c_pn as bom_pn, bom.c_name as bom_name, bom.c_type as bom_type, bom_unit.c_id as bom_ut_id, bom_unit.c_name as bom_ut_name, bom_unit.c_sub_name as bom_ut_sub_name, bom_unit.c_ratio as bom_ut_ratio from pn_tbl as pn left join unit_tbl as ut on pn.c_unit_id = ut.c_id left join pn_cls_relation_tbl as pn_pn_cls on pn.c_id = pn_pn_cls.c_pn_id left join pn_cls_tbl as pn_cls on pn_pn_cls.c_cls_id = pn_cls.c_id left join pn_bom_relation_tbl as pn_bom_rel on pn.c_id = pn_bom_rel.c_pn_id and pn_pn_cls.c_cls_id = pn_bom_rel.c_cls_id left join bom_tbl as bom on pn_bom_rel.c_bom_id = bom.c_id left join unit_tbl as bom_unit on bom.c_unit_id = bom_unit.c_id
and pn.c_id = #{id}
and pn.c_pn like '%${pn}%'
and pn.c_pn = #{pn}
order by pn.c_pn asc

select pn_cls.c_id as pn_cls_id, pn_cls.c_name as pn_cls_name from pn_cls_tbl as pn_cls left join pn_cls_relation_tbl as pn_cls_relation on pn_cls_relation.c_cls_id = pn_cls.c_id left join pn_tbl as pn on pn_cls_relation.c_pn_id = pn.c_id
and pn.c_id = #{id}
order by pn_cls.c_name asc

mapper.insert:
		insert into pn_cls_relation_tbl
		(
			c_pn_id, 
			c_cls_id
		)
		values

        (
	        #{id},
            #{pnCls.id}
        )

mapper.update:
    	update pn_tbl
		set	c_pn = #{pn},
			c_name = #{name},
			c_unit_id = #{unit.id}

			c_id = #{id}

update.foreach:
    	update pn_bom_relation_tbl

            c_use_num=${bomRel.useNum}


			c_pn_id = #{id} and c_bom_id = #{bomRel.bom.id}
