mapper.select:
select delivery.c_id as d_id, delivery.c_no as d_no, delivery.c_delivery_time as d_delivery_time, delivery.c_create_time as d_create_time, delivery.c_update_time as d_update_time, delivery.c_state as d_state, delivery.c_type as d_type, delivery.c_comment as d_comment, user.c_id as user_id, user.c_no as user_no, user.c_name as user_name, upt_user.c_id as upt_user_id, upt_user.c_no as upt_user_no, upt_user.c_name as upt_user_name from delivery_tbl as delivery left join user_tbl as user on delivery.c_user_id = user.c_id left join user_tbl as upt_user on delivery.c_update_user_id = upt_user.c_id
and delivery.c_id = #{id}
and delivery.c_type=#{type}
and delivery.c_state=#{state}
and delivery.c_no like '%${no}%'
and delivery.c_no = #{no}
and delivery.c_user_id = #{user.id}
order by delivery.c_state asc, delivery.c_entry_time desc

mapper.update:
    	update delivery_tbl
		set	c_state = #{state},
			c_delivery_time = #{deliveryTime}

			c_id = #{id}
