mapper.select:
select entry.c_id as e_id, entry.c_entry_time as e_entry_time, entry.c_update_time as e_update_time, entry.c_create_time as e_create_time, entry.c_state as e_state, entry.c_type as e_type, entry.c_comment as e_comment, user.c_id as user_id, user.c_no as user_no, user.c_name as user_name, upt_user.c_id as upt_user_id, upt_user.c_no as upt_user_no, upt_user.c_name as upt_user_name, e_pn.c_id as e_pn_id, e_pn.c_produced_num as e_pn_produced_num, e_pn.c_semi_produced_num as e_pn_semi_produced_num, o_pn.c_id as o_pn_id, o_pn.c_num as o_pn_num, pn.c_id as pn_id, pn.c_pn as pn_pn, pn.c_name as pn_name, pn_cls.c_id as pn_cls_id, pn_cls.c_name as pn_cls_name, unit.c_id as unit_id, unit.c_name as unit_name, o.c_id as o_id, o.c_no as o_no, wh_pn.c_id as wh_pn_id, wh_pn.c_produced_num as wh_pn_produced_num, wh_pn.c_semi_produced_num as wh_pn_semi_produced_num from entry_tbl as entry left join user_tbl as user on entry.c_user_id = user.c_id left join user_tbl as upt_user on entry.c_update_user_id = upt_user.c_id left join entry_pn_tbl as e_pn on entry.c_id = e_pn.c_entry_id left join order_pn_tbl as o_pn on e_pn.c_ref_id = o_pn.c_id left join pn_tbl as pn on o_pn.c_pn_id = pn.c_id left join unit_tbl as unit on pn.c_unit_id = unit.c_id left join pn_cls_tbl as pn_cls on o_pn.c_pn_cls_id = pn_cls.c_id left join order_tbl as o on o_pn.c_order_id = o.c_id left join warehouse_pn_tbl as wh_pn on e_pn.c_ref_id = wh_pn.c_order_pn_id
entry.c_type=0
and entry.c_id=#{id}

select entry.c_id as e_id, entry.c_entry_time as e_entry_time, entry.c_update_time as e_update_time, entry.c_create_time as e_create_time, entry.c_state as e_state, entry.c_type as e_type, entry.c_comment as e_comment, user.c_id as user_id, user.c_no as user_no, user.c_name as user_name, upt_user.c_id as upt_user_id, upt_user.c_no as upt_user_no, upt_user.c_name as upt_user_name, e_bom.c_id as e_bom_id, e_bom.c_num as e_bom_num, bom.c_id as bom_id, bom.c_pn as bom_pn, bom.c_name as bom_name, bom.c_type as bom_type, unit.c_id as unit_id, unit.c_name as unit_name, wh_bom.c_id as wh_bom_id, wh_bom.c_num as wh_bom_num from entry_tbl as entry left join user_tbl as user on entry.c_user_id = user.c_id left join user_tbl as upt_user on entry.c_update_user_id = upt_user.c_id left join entry_bom_tbl as e_bom on entry.c_id = e_bom.c_entry_id left join bom_tbl as bom on e_bom.c_ref_id = bom.c_id left join unit_tbl as unit on bom.c_unit_id = unit.c_id left join warehouse_bom_tbl as wh_bom on e_bom.c_ref_id = wh_bom.c_bom_id
entry.c_type=1
and entry.c_id=#{id}

mapper.update:
		update entry_tbl
		set c_state=#{state},
			c_entry_time=#{entryTime}

      		c_id=#{id}
