mapper.select:
select e_pn.c_id as e_pn_id, e_pn.c_entry_id as e_pn_entry_id, e_pn.c_produced_num as e_pn_produced_num, e_pn.c_semi_produced_num as e_pn_semi_produced_num from entry_pn_tbl as e_pn
and e_pn.c_entry_id=#{wareHouseEntry.id}
and e_pn.c_ref_id=#{pn.id}

mapper.update:
    	update entry_pn_tbl
    	set c_produced_num=#{producedNum},
    		c_semi_produced_num=#{semiProducedNum}


				and c_id=#{id}


				and c_entry_id=#{wareHouseEntry.id}
				and c_ref_id=#{pn.id}
